import React, { useEffect } from 'react';

const PaymentForm = ({ checkoutId, shopperResultUrl }) => {
  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://testsecure.peachpayments.com/v2/checkout/js?checkoutId=${checkoutId}`;
    script.async = true;
    script.onload = () => {
      // Optionally, configure the payment form after the script loads
      window.PeachPayments.init({
        shopperResultUrl: shopperResultUrl,
        // Other configuration options can go here
      });
    };
    document.body.appendChild(script);

    return () => {
      // Cleanup the script when the component unmounts
      document.body.removeChild(script);
    };
  }, [checkoutId, shopperResultUrl]);

  return (
    <div id="payment-form-container">
      {/* The payment form will be injected into this div */}
    </div>
  );
};

export default PaymentForm;
