import React, { useEffect, useRef, useState } from 'react';

function PaymentForm({ checkoutId }) {
  const containerRef = useRef(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Define wpwlOptions globally
    window.wpwlOptions = {
      style: "card",
      locale: "en",
      onError: function(error) {
        console.error('Payment widget error:', error);
        setError('An error occurred with the payment form. Please try again.');
      }
    };

    const script = document.createElement('script');
    const scriptSrc = `https://testsecure.peachpayments.com/v2/checkout/js?checkoutId=${checkoutId}`;
    
    script.src = scriptSrc;
    script.async = true;

    console.log('Loading script from:', scriptSrc);
    alert(`Loading script from: ${scriptSrc}`);

    script.onload = () => {
      console.log('Script loaded successfully.');
      try {
        const form = document.createElement('form');
        form.setAttribute('action', 'https://myplusplus.com');
        form.setAttribute('class', 'paymentWidgets');
        form.setAttribute('data-brands', 'VISA MASTER AMEX');
        
        if (containerRef.current) {
          containerRef.current.innerHTML = '';
          containerRef.current.appendChild(form);
        }

        if (window.wpwlOptions) {
          window.wpwlOptions.onSuccess = function (result) {
            console.log('Payment successful:', result);
          };
          window.wpwlOptions.onFail = function (error) {
            console.error('Payment failed:', error);
          };
        }

        // Initialize the payment form
        if (window.wpwl) {
          window.wpwl.init();
        } else {
          console.error('wpwl object not found.');
          setError('Payment form could not be initialized.');
        }

      } catch (e) {
        console.error('Error while creating or appending the form:', e);
        setError('An error occurred while setting up the payment form. Please try again.');
      }
    };

    script.onerror = () => {
      console.error('Failed to load the payment form script.');
      setError('Failed to load the payment form. Please try again later.');
    };
    
    document.head.appendChild(script);

    return () => {
      // Cleanup: remove the script when component unmounts
      if (script.parentNode) {
        script.parentNode.removeChild(script);
      }
      // Remove global wpwlOptions
      delete window.wpwlOptions;
    };
  }, [checkoutId]);

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  return <div ref={containerRef}></div>;
}

export default PaymentForm;
