import React, { useState } from 'react';
import axios from 'axios';
import PaymentForm from '../components/PaymentForm';

function PaymentPage() {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [checkoutUrl, setCheckoutUrl] = useState(null);

  const initializePayment = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await axios.get('https://e5ghgaa4n9.execute-api.af-south-1.amazonaws.com/dev/payment');
      setCheckoutUrl(response.data); 
    } catch (err) {
      console.error('Error calling Lambda:', err);
      setError('Failed to initiate payment. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="PaymentPage">
      <h1>Payment Page</h1>
      {!checkoutUrl && (
        <button onClick={initializePayment} disabled={isLoading}>
          {isLoading ? 'Loading...' : 'Pay Now'}
        </button>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {checkoutUrl && <PaymentForm checkoutUrl={checkoutUrl} />}
    </div>
  );
}

export default PaymentPage;
