// PaymentForm.js
import React, { useEffect, useRef } from 'react';

const PaymentForm = ({ entityId, checkoutId }) => {
  const paymentFormRef = useRef(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://sandbox-checkout.peachpayments.com/js/checkout.js';
    script.async = true;
    script.onload = initializeCheckout;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, [entityId, checkoutId]);

  const initializeCheckout = () => {
    const checkout = window.Checkout.initiate({
      key: entityId,
      checkoutId: checkoutId,
      events: {
        onCompleted: (event) => console.log('Completed:', event),
        onCancelled: (event) => console.log('Cancelled:', event),
        onExpired: (event) => console.log('Expired:', event),
      },
      options: {
        theme: {
          brand: {
            primary: "#ff0000",
          },
          cards: {
            background: "#00ff00",
            backgroundHover: "#F3F3F4",
          }
        },
        ordering: {
          PAYFLEX: 1,
          CARD: 2,
        },
      },
    });

    checkout.render("#payment-form");
  };

  return <div id="payment-form" ref={paymentFormRef}></div>;
};

export default PaymentForm;
