import React, { useEffect } from 'react';

const PaymentForm = ({ checkoutId, shopperResultUrl }) => {
  console.log('PaymentForm component rendered with checkoutId:', checkoutId);

  useEffect(() => {
    console.log('PaymentForm useEffect triggered');

    // Define the wpwlOptions global object
    window.wpwlOptions = {
      style: "card",
      onReady: function() {
        console.log('wpwlOptions onReady callback');
      }
      // Add more options as needed
    };

    const script = document.createElement('script');
    script.src = `https://eu-test.oppwa.com/v1/paymentWidgets.js?checkoutId=${checkoutId}`;
    script.async = true;

    script.onload = () => {
      console.log('Payment widget script loaded successfully');
    };

    script.onerror = (error) => {
      console.error('Error loading payment widget script:', error);
    };

    document.body.appendChild(script);

    return () => {
      console.log('Cleaning up script');
      document.body.removeChild(script);
    };
  }, [checkoutId]);

  return (
    <div>
      <form action={shopperResultUrl} className="paymentWidgets" data-brands="VISA MASTER AMEX"></form>
    </div>
  );
};

export default PaymentForm;
