import React, { useEffect, useRef, useState } from 'react';

function PaymentForm({ checkoutId }) {
  const containerRef = useRef(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Define wpwlOptions globally
    window.wpwlOptions = {
      style: "card",
      locale: "en",
      onError: function(error) {
        console.error('Payment widget error:', error);
        setError('An error occurred with the payment form. Please try again.');
      }
      // Add any other configuration options here
    };

    const script = document.createElement('script');
    script.src = `https://test.oppwa.com/v2/checkout/paymentWidgets.js?checkoutId=${checkoutId}`;    script.async = true;
    script.onload = () => {
      const form = document.createElement('form');
      form.setAttribute('action', 'https://myplusplus.com');
      form.setAttribute('class', 'paymentWidgets');
      form.setAttribute('data-brands', 'VISA MASTER AMEX');
      
      if (containerRef.current) {
        containerRef.current.innerHTML = '';
        containerRef.current.appendChild(form);
      }
    };
    script.onerror = () => {
      setError('Failed to load the payment form. Please try again later.');
    };
    document.head.appendChild(script);

    return () => {
      // Cleanup: remove the script when component unmounts
      if (script.parentNode) {
        script.parentNode.removeChild(script);
      }
      // Remove global wpwlOptions
      delete window.wpwlOptions;
    };
  }, [checkoutId]);

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  return <div ref={containerRef}></div>;
}

export default PaymentForm;
